#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Wed Dec  4 01:13:05 2024@author: noahwuhrmann"""# Verison 6import pandas as pdimport streamlit as stimport reimport io# Streamlit Appst.title("Excel Sortier- und Exporttool")# Dateiuploaduploaded_file = st.file_uploader("Bitte eine Excel-Datei hochladen", type=["xlsx"])if uploaded_file:    # Mitteilung über erfolgreichen Upload    st.success("Die Datei wurde erfolgreich hochgeladen.")        # Datei in einen DataFrame laden    try:        df = pd.read_excel(uploaded_file, engine='openpyxl')                # Vorgabe: Fixe Spalten, die immer im Export enthalten sein sollen        fixed_columns = ["Bestand", "Artikelname", "KatalogNr", "Artikel", "Artikelgruppe", "Netto"]                # Monatsspalten erkennen        month_columns = []        month_pattern = re.compile(r"(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)\s?\d{4}")        # Überprüfen, welche Spalten Monatsspalten sind        for col in df.columns:            if month_pattern.match(col):                month_columns.append(col)        # Spalten sortieren: Fixe Spalten zuerst, dann die Monatsspalten        target_columns = fixed_columns + month_columns        # Entfernen aller Spalten, die nicht in target_columns sind        filtered_df = df[[col for col in target_columns if col in df.columns]]                # Nutzer auffordern, den Dateinamen anzugeben        output_filename = st.text_input("Bitte geben Sie einen Namen für die exportierte Datei ein (ohne .xlsx):", value="Exportierte_Datei")                if st.button("Excel-Datei exportieren"):            # Excel-Datei speichern und als Download anbieten            buffer = io.BytesIO()            with pd.ExcelWriter(buffer, engine='openpyxl') as writer:                filtered_df.to_excel(writer, index=False, sheet_name="Export")            st.success(f"Die Datei '{output_filename}.xlsx' wurde erfolgreich erstellt und ist bereit zum Herunterladen.")            st.download_button(                label="Herunterladen",                data=buffer.getvalue(),                file_name=f"{output_filename}.xlsx",                mime="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"            )    except Exception as e:        st.error(f"Ein Fehler ist aufgetreten: {e}")