#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Wed Dec  4 01:13:05 2024@author: noahwuhrmann"""# Version 1import pandas as pdimport streamlit as stimport reimport io# Streamlit Appst.title("Excel Sortier- und Exporttool")# Dateiuploaduploaded_file = st.file_uploader("Bitte eine Excel-Datei hochladen", type=["xlsx"])if uploaded_file:    st.success("Die Datei wurde erfolgreich hochgeladen.")        try:        # Daten aus der hochgeladenen Datei laden        df = pd.read_excel(uploaded_file, engine='openpyxl')                # Definierte fixe Spalten        fixed_columns = ["Bestand", "Artikelname", "KatalogNr", "Artikel", "Artikelgruppe", "Netto"]                # Dynamische Spalten (aktuelle und vergangene Jahre, Monate)        current_year = pd.Timestamp.now().year        last_year = current_year - 1                # Jahres- und Monatsspalten identifizieren        year_columns = [str(current_year), str(last_year)]        month_columns = []                # Muster für Monatsnamen erkennen        month_pattern = re.compile(r"(Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov|Dec)(\d{4})")        for col in df.columns:            if month_pattern.match(col):                month_columns.append(col)                # Monatsspalten sortieren (neueste zuerst)        month_columns.sort(key=lambda x: (int(x[-4:]), x[:3]), reverse=True)                # Zielspalten definieren        target_columns = fixed_columns + year_columns + month_columns                # Daten filtern        filtered_df = df[[col for col in target_columns if col in df.columns]]                # Nutzeraufforderung für Dateinamen        output_filename = st.text_input("Bitte geben Sie einen Namen für die exportierte Datei ein (ohne .xlsx):", value="Exportierte_Datei")                if st.button("Excel-Datei exportieren"):            # Datei in den Speicher schreiben            output = io.BytesIO()            with pd.ExcelWriter(output, engine='openpyxl') as writer:                filtered_df.to_excel(writer, index=False, sheet_name="Export")                        # Datei bereitstellen            st.success(f"Die Datei '{output_filename}.xlsx' wurde erfolgreich erstellt und ist bereit zum Herunterladen.")            st.download_button(                label="Herunterladen",                data=output.getvalue(),                file_name=f"{output_filename}.xlsx",                mime="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"            )    except Exception as e:        st.error(f"Ein Fehler ist aufgetreten: {e}")